          year: 'numeric', month: 'numeric', day: 'numeric',

          hour: 'numeric', minute: 'numeric', second: 'numeric'

    }).format(selectedLanguage).toString();
    console.log(date)
  },[selectedLanguage])
  return (
    <>
        <select className="selectbox" onChange={(e) => setSelectedLanguage(e.target.value)}>
          <option key={1} value={""}>{"Pleace select time zone..."}</option>
          {langs && langs.length > 0 && langs.map((lang) => {
              return(
                <option key={lang} value={lang}>{lang}</option>
              )
          })}
        </select>
        <div className="result">
        {selectedLanguage ? selectedLanguage : "Pleace select time zone..."}
        </div>
    </>
  );
};

export default RenderSelectBox;
